import React from 'react'

const Statistics = ({title, stats}) => {
    return (
       <section className="statistics">
  <h2 className="title">{title}</h2>

  <ul className="stat-list">
            {stats.map(stat => <StatisticsItems stat={stat}/>)}
  </ul>
</section> 
    );
}

export default Statistics;
